import React from 'react';
import TableRow from './tablerow';
import Calculate from './components/calculate';
import Reset from './components/reset';

class Table extends React.Component {


    constructor(props) {
        super(props);
    }

    handleSubmit(event) {
        alert("Type1: " + this.state.type1 + "\nType2: " + this.state.type2)
        event.preventDefault();
    }

    reset(){
        this.state = {type1: '0', type2: '0'}
    }

    render(){
        return (
            <div className="row">
            <div className="col-lg-8 offset-lg-2 col-12 fs-4">
            <table className="table table-dark table-hover mt-5">
                <thead>
                    <tr>
                    <th scope="col">Pokemon Type/s</th>
                    <th scope="col">Weakness</th>
                    <th scope="col">Resistance</th>
                    </tr>
                </thead>
                <tbody>
                    <TableRow options={this.props.options} reset={this.reset}/>
                    {/* <TableRow options={this.props.options}/>
                    <TableRow options={this.props.options}/>
                    <TableRow options={this.props.options}/>
                    <TableRow options={this.props.options}/>
                    <TableRow options={this.props.options}/> */}
                </tbody>
            </table>
            </div>

            {/* Buttons */}
            <div className="row text-end m-0 p-0">
                <div className="col-12 col-lg-8 offset-lg-2">
                        <Calculate />
                        <Reset />
                </div>
            </div>
            </div>

        );
    }
};

export default Table;
