import React from 'react';
import Weakness from './weakness';
import Resistance from './resistance';

class TableRow extends React.Component {

    constructor(props) {
        super(props);
        this.state = {type1: '0', type2: '0'}

        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        this.setState({
            ...this.state,
            [event.target.name]: event.target.value
        });
    }

    reset = () => {
        this.setState ({type1: '0', type2: '0'})
    }

    render() {
        return (
                    <tr>
                    <td className="text-dark">
                        <select name="type1" className="form-select" aria-label=".form-select-sm example" onChange={this.handleChange}>
                            {this.props.options.map((type, index) => (
                                <option key={index} value={type.value}>{type.type}</option>
                            ))}
                        </select>
                        <select name="type2" className="form-select" aria-label=".form-select-sm example" onChange={this.handleChange}>
                            {this.props.options.map((type, index) => (
                                <option key={index} value={type.value}>{type.type}</option>
                            ))}
                        </select>
                    </td>
                    <Weakness type1={this.state.type1} type2={this.state.type2} />
                    <Resistance type1={this.state.type1} type2={this.state.type2}/>
                    </tr>
        );
    }
};

export default TableRow;
