import React from 'react';
import TableRow from './tablerow';

const options = [
    {value: "0", type: "None"},
    {value: "1", type: "Normal"},
    {value: "2", type: "Fire"},
    {value: "3", type: "Water"},
    {value: "4", type: "Electric"},
    {value: "5", type: "Grass"},
    {value: "6", type: "Ice"},
    {value: "7", type: "Fighting"},
    {value: "8", type: "Poison"},
    {value: "9", type: "Ground"},
    {value: "10", type: "Flying"},
    {value: "11", type: "Psychic"},
    {value: "12", type: "Bug"},
    {value: "13", type: "Rock"},
    {value: "14", type: "Ghost"},
    {value: "15", type: "Dragon"},
    {value: "16", type: "Dark"},
    {value: "17", type: "Steel"},
    {value: "18", type: "Fairy"}
  ]

class Table extends React.Component {


    constructor(props) {
        super(props);
        this.state = [
            {slot0_type1: '0',
             type2: '0'}
        ]

        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        this.setState({
            ...this.state,
            [event.target.name]: event.target.value
        });
    }

    reset(){
        this.setState({
            ...this.state,
            type1: '0', 
            type2: '0'
        });
    }

    render(){
        return (
            <div className="row">
            <div className="col-lg-8 offset-lg-2 col-12 fs-4">
            <table className="table table-dark table-hover mt-5">
                <thead>
                    <tr>
                    <th scope="col">Pokemon Type/s</th>
                    <th scope="col">Weakness</th>
                    <th scope="col">Resistance</th>
                    </tr>
                </thead>
                <tbody>
                    <TableRow options={options} type1={this.state.type1} type2={this.state.type2} handleChange={this.handleChange}/>
                    <TableRow options={options} type1={this.state.type1} type2={this.state.type2} handleChange={this.handleChange}/>
                    <TableRow options={options} type1={this.state.type1} type2={this.state.type2} handleChange={this.handleChange}/>
                    <TableRow options={options} type1={this.state.type1} type2={this.state.type2} handleChange={this.handleChange}/>
                    <TableRow options={options} type1={this.state.type1} type2={this.state.type2} handleChange={this.handleChange}/>
                    <TableRow options={options} type1={this.state.type1} type2={this.state.type2} handleChange={this.handleChange}/>
                </tbody>
            </table>
            </div>

            {/* Buttons */}
            <div className="row text-end m-0 p-0">
                <div className="col-12 col-lg-8 offset-lg-2">
                        <button type="button" onClick={this.reset} className="btn btn-dark btn-lg">Reset</button>
                </div>
            </div>
            </div>

        );
    }
};

export default Table;
