                    <TableRow options={this.props.options}/>
                </tbody>
            </table>
            </div>

            {/* Buttons */}
            <div className="row text-end m-0 p-0">
                <div className="col-12 col-lg-8 offset-lg-2">
                        <button type="button" onClick={this.resetRows} className="btn btn-dark btn-lg">Reset</button>
                </div>
            </div>
            </div>

        );
    }
};

export default Table;
