import './App.css';
import './custom.scss'
import NavBar from './components/navbar';
import Table from './components/table';
import {Helmet} from "react-helmet";
import Calculate from './components/calculate';
import Reset from './components/reset';

const options = [
  {value: "0", type: "None"}
]

function App() {
  return (
    <div className="custom-container h-100 w-100">
      <Helmet>
        <meta charSet="utf-8" />
        <title>Pokemon Team Builder</title>
      </Helmet>
      {/* Header */}
      <div className="container-fluid border-top border-dark border-5 bg-danger p-4">
        <div className="row justify-content-center">
          <div className="col-4">
          <NavBar />
          </div>
        </div>
      {/* Table */}
        <div className="row">
          <div className="col-lg-8 offset-lg-2 col-12 fs-4">
            <Table options={options}/>
          </div>
        {/* Buttons */}
        <div className="row text-end m-0 p-0">
          <div className="col-12 col-lg-8 offset-lg-2">
            <Calculate />
            <Reset />
          </div>
        </div>
        </div>
      </div>
      {/* Results */}
      <div className="container-fluid border-top border-dark border-5 p-4 bg-light h-50 text-danger">
        <div className="row">
          <div className="col fw-bold fs-2">
            <div>Un-Resisted: <span className="text-dark">Water, Ground</span></div>
            <div>Recommended Additions: <span className="text-dark">Grass or Electric , Dragon or Fairy</span></div>
            <div>Obsolete Pokemon: <span className="text-dark">Pokemon 5, Pokemon 2</span></div>
            <div>Rating: <span className="text-dark">Average</span></div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
